.panels > *{
    margin: 0;
    width: 100%;
    transition: transform 0.5s;
    flex : 1 0 auto ;
    display:flex;
    align-items: center;
    justify-content: center;
}
.panels>*:first-child{
    transform: translateY(-100%);    

}
.panels.open-active>*:first-child{
    transform: translateY(0%);    

}
.panels>*:last-child{
        transform: translateY(100%);
}
.panels.open-active>*:last-child{
    transform: translateY(0%);
}

.panel .open {
    flex:5;
}
.panels .open-active p:first-child{
    transform: translateY(0%);
}
.panels .open-active p:last-child{
    transform: translateY(0%);
}
